import React from "react";
import axios from "axios";
import Facebook from "../imagenes/Facebook.png";
import Google from "../imagenes/Google.png";
import Twitter from "../imagenes/Twitter.png";
import {
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  Table,
  Button,
  Container,
  FormGroup,
} from "reactstrap";
import md5 from "md5";

const url = "https://ftp-backend.herokuapp.com/usuarios";

export default class MainRegistro extends React.Component {
  constructor() {
    super();
    this.state = {
      user: {
        nombre: "",
        apellido: "",
        correo: "",
        usuario: "",
        contraseña: "",
        ccontraseña: "",
      },
    };
  }

  handleChange = async (e) => {
    await this.setState({
      user: {
        ...this.state.user,
        [e.target.name]: e.target.value,
      },
    });
  };

  handleSubmit(e) {
    e.preventDefault();
  }

  verificarUsuario = () => {
    var user = this.state.user;
    var nombre = user.nombre;
    var apellido = user.apellido;
    var correo = user.correo;
    var usuario = user.usuario;
    var contraseña = user.contraseña;
    var ccontraseña = user.ccontraseña;
    if (
      nombre.length === 0 ||
      apellido.length === 0 ||
      correo.length === 0 ||
      usuario.length === 0 ||
      contraseña.length === 0 ||
      ccontraseña.length === 0
    ) {
      document.getElementById("Mensaje1").style.display = "block";
    } else {
      //  axios.get(url, { correo: correo }).then((res) => {
      //    if (res.length > 0) {
      //      document.getElementById("Mensaje2").style.display = "block";
      //    }else{
      this.crearUsuario();
      //    }
      //  });
    }
  };

  crearUsuario = async () => {
    const user = this.state.user;
    await axios
      .post(url, {
        nombre: user.nombre,
        apellido: user.apellido,
        correo: user.correo,
        usuario: user.usuario,
        contraseña: md5(user.contraseña),
      })
      .then(() => {
        window.location.href = "./";
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col my-5 py-5">
            <Modal>
              <ModalHeader>Regístrate Ya!</ModalHeader>
              <ModalBody>
                <FormGroup>
                  <label>Nombre</label>
                  <input 
                  className="form-control"
                  name="nombre"
                  value={this.state.user.nombre}
                  />
                </FormGroup>
              </ModalBody>
              <ModalFooter></ModalFooter>
            </Modal>
          </div>
        </div>
      </div>
    );
  }
}
