import React from "react";
import axios from "axios";
import Facebook from "../imagenes/Facebook.png";
import Google from "../imagenes/Google.png";
import Twitter from "../imagenes/Twitter.png";

export default class MainRegistro extends React.Component {
  constructor() {
    super();
    this.state = {
        nombre: "",
        apellido: "",
        correo: "",
        usuario: "",
        contraseña: "",
    };
    this.handleInputChange = this.handleInputChange.bind(this);
    this.registro=()=>{
      axios.post("https://n3922.sse.codesandbox.io/api/nuevo_usuario", this.state.nombre, this.state.apellido, this.state.correo, this.state.usuario, this.state.contraseña)
    };
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col my-5 py-5">
            <form action={this.registro} className="container tamañoMaximo py-3 bg-dark rounded text-white">
              <h4 className="text-center pb-3 w-100 border-bottom">
                Regístrate Ya!
              </h4>
              <div className="form-group">
                <label for="exampleInputEmail1">Nombre</label>
                <input
                  type="text"
                  value={this.state.nombre}
                  onChange={this.handleInputChange}
                  className="form-control"
                  name="nombre"
                  aria-describedby="emailHelp"
                  placeholder="David Jesús"
                />
              </div>
              <div className="form-group">
                <label for="exampleInputEmail1">Apellido</label>
                <input
                  type="text"
                  value={this.state.apellido}
                  onChange={this.handleInputChange}
                  className="form-control"
                  name="apellido"
                  aria-describedby="emailHelp"
                  placeholder="Correa Castillo"
                />
              </div>
              <div className="form-group">
                <label for="exampleInputEmail1">Usuario</label>
                <input
                  type="text"
                  value={this.state.usuario}
                  onChange={this.handleInputChange}
                  className="form-control"
                  name="usuario"
                  aria-describedby="emailHelp"
                  placeholder="DavidJCC15"
                />
              </div>
              <div className="form-group">
                <label for="exampleInputEmail1">Correo Electrónico</label>
                <input
                  type="email"
                  value={this.state.correo}
                  onChange={this.handleInputChange}
                  className="form-control"
                  name="correo"
                  aria-describedby="emailHelp"
                  placeholder="ejemplo@gmail.com"
                />
              </div>
              <div className="form-group">
                <label for="exampleInputPassword1">Contraseña</label>
                <input 
                  type="password"
                  value={this.state.contraseña}
                  onChange={this.handleInputChange}
                  className="form-control"
                  name="contraseña" />
              </div>
              <div className="form-group">
                <label for="exampleInputPassword1">Confirmar Contraseña</label>
                <input type="password" className="form-control" id="CPassword" />
              </div>
              <div className="d-flex justify-content-between">
                <div className="">
                  <a href="#popis">
                    <img
                      src={Facebook}
                      alt="..."
                      className="img-fluid"
                      width="35"
                    />
                  </a>
                  <a href="#popis">
                    <img
                      src={Google}
                      alt="..."
                      className="img-fluid"
                      width="30"
                    />
                  </a>
                  <a href="#popis">
                    <img
                      src={Twitter}
                      alt="..."
                      className="img-fluid"
                      width="30"
                    />
                  </a>
                </div>
                <button type="submit" class="btn btn-sm btn-success m-1">
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}
