import React from "react";
import Pregunta from "./Pregunta";
import pr from "../server/preguntas.json";

export default class Main extends React.Component {
  resultadoTest=()=>{
    var A = 0;
    var B = 0;
    var C = 0;
    var D = 0;
    var E = 0;
    for(var x = 1; x < 25; x++){
      var y = preg.grupo.value;

    }
  }
  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col my-5 py-5">
            <h4 className="text-white text-center">
              Responde las preguntas de acuerdo a tu personalidad y con toda
              sinceridad.
            </h4>
            <p className="text-white text-center">
              Si deseas cancelar este test, dirígete al boton de cancelar al
              final de la pantalla.
            </p>
            {pr.preguntas.map(x=><div key={x.id} ><div className="card container my-3">
        <div className="card-header text-center">
          <strong>Pregunta #{preg.id}</strong>
        </div>
        <div className="card-body">
          <h6>{preg.pregunta}</h6>
          <div className="form-check">
            <input
              className="form-check-input"
              type="radio"
              name={preg.grupo}
              id="exampleRadios1"
              value={1}
            />
            <label className="form-check-label" htmlFor="exampleRadios1">
              {preg.opcA}
            </label>
          </div>
          <div className="form-check">
            <input
              className="form-check-input"
              type="radio"
              name={preg.grupo}
              id="exampleRadios2"
              value={2}
            />
            <label className="form-check-label" htmlFor="exampleRadios1">
              {preg.opcB}
            </label>
          </div>
          <div className="form-check">
            <input
              className="form-check-input"
              type="radio"
              name={preg.grupo}
              id="exampleRadios3"
              value={3}
            />
            <label className="form-check-label" htmlFor="exampleRadios1">
              {preg.opcC}
            </label>
          </div>
          <div className="form-check">
            <input
              className="form-check-input"
              type="radio"
              name={preg.grupo}
              id="exampleRadios4"
              value={4}
            />
            <label className="form-check-label" htmlFor="exampleRadios1">
              {preg.opcD}
            </label>
          </div>
          <div className="form-check">
            <input
              className="form-check-input"
              type="radio"
              name={preg.grupo}
              id="exampleRadios5"
              value={5}
            />
            <label className="form-check-label" htmlFor="exampleRadios1">
              {preg.opcE}
            </label>
          </div>
        </div>
      </div></div>)}
            <div className="d-flex justify-content-between">
              <a
                href="./Cuenta"
                type="submit"
                className="btn btn-sm btn-success m-1"
              >
                Cancel
              </a>
              <a
                href="./Resultado"
                type="submit"
                className="btn btn-sm btn-success m-1"
              >
                Submit
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
