import React, { useState } from "react";
import Axios from "axios";
import md5 from "md5";

export default function MainRegistro() {
  const [correoElectronicoReg, setCorreoElectronicoReg] = useState("");
  const [contrasenaReg, setContrasenaReg] = useState("");
  const [nombreReg, setNombreReg] = useState("");
  return (
    <div className="App">
      <div className="registration">
        <h1>Registro</h1>
        <label>Correo electronico</label>
        <input
          type="email"
          onChange={(e) => {
            setCorreoElectronicoReg(e.target.value);
          }}
        />
        <label>Nombre completo </label>

        <input
          type="text"
          onChange={(e) => {
            setNombreReg(e.target.value);
          }}
        />

        <label>Password</label>
        <input
          type="text"
          onChange={(e) => {
            setContraseñaReg(e.target.value);
          }}
        />
        <button onClick={register}> Registrar </button>
      </div>
    </div>
  );
}

const register = () => {
  Axios.post(
    "https://tienda-ahorro-backend.herokuapp.com/api/registroUsuario",
    {
      CorreoElectronico: correoElectronicoReg,
      Nombre: nombreReg,
      Contrasena: md5(contrasenaReg),
    }
  ).then((response) => {
    console.log(response.data);
  });
};
